sourcesContent:
/* eslint-disable react/prop-types */
import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import {
  Document,
  Page,
  Text,
  View,
  StyleSheet,
  Font,
} from '@react-pdf/renderer';

import moment from 'moment';

import LoraMedium from 'app/assets/fonts/LoraMedium.ttf';
import LoraBold from 'app/assets/fonts/LoraBold.ttf';
import LoraItalic from 'app/assets/fonts/LoraItalic.ttf';
import LoraBoldItalic from 'app/assets/fonts/LoraBoldItalic.ttf';

import nations from 'app/assets/mock/nations.json';
import locations from 'app/assets/mock/locations.json';

Font.register({
  family: 'LoraMedium',
  src: LoraMedium,
});
Font.register({
  family: 'LoraBold',
  src: LoraBold,
});
Font.register({
  family: 'LoraItalic',
  src: LoraItalic,
});
Font.register({
  family: 'LoraBoldItalic',
  src: LoraBoldItalic,
});

const styles = StyleSheet.create({
  page: {
    flexDirection: 'row',
    fontSize: '13px',
    flexWrap: 'wrap',
    fontFamily: 'LoraMedium',
    paddingTop: 50,
  },
  section: {
    margin: '0 10px 0 10px',
    padding: '0 10px 0 10px',
    flexGrow: 1,
    alignItems: 'center',
  },
  center: {
    textAlign: 'center',
  },
  bold: {
    fontFamily: 'LoraBold',
  },
  italic: {
    fontFamily: 'LoraItalic',
  },
  boldItalic: {
    fontFamily: 'LoraBoldItalic',
  },
  paddingBottom2: {
    paddingBottom: 2,
  },
  marginLeft50: {
    marginLeft: 50,
  },
  left50: {
    left: 50,
  },
  paddingLeft100: {
    paddingLeft: 100,
  },
  paddingLeft11: {
    paddingLeft: 11,
  },
  paddingLeft21: {
    paddingLeft: 21,
  },
  paddingTop8: {
    paddingTop: 8,
  },
  marginTop40: {
    marginTop: 40,
  },
  paddingVertical15: {
    paddingVertical: '15px 0 5px 0',
  },
  borderBottom: {
    borderBottom: '1px solid black',
  },
  paddingHorizontal40: {
    paddingHorizontal: 40,
    textAlign: 'justify',
    lineHeight: 1.8,
  },
  paddingHorizontal50: {
    paddingHorizontal: 50,
    textAlign: 'justify',
    lineHeight: 1.8,
  },
  footer: {
    width: '100%',
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingLeft: 50,
    paddingRight: 100,
    marginTop: 10,
  },
  signature: {
    width: '100%',
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingLeft: 50,
    paddingRight: 90,
  },
  tableWidth: {
    width: '100vw',
    right: 32.5,
    paddingRight: 32.5,
  },
});

// const Table = ({ children }) => (
