              if (!ward) {
                return 'Phường/Xã/Thị trấn không hợp lệ';
              }
            }
            return false;
          },
        },
        {
          header: 'KP/ấp',
          assign: 'quarter',
          validate: (__, value) => {
            if (!value) {
              return 'Bắt buộc nhập khu phố/ấp';
            }
            return false;
          },
        },
        {
          header: 'Tổ(*)',
          assign: 'quarterGroup',
