            gender: d.gender,
            cmnd: d.identityCard.length !== 12 ? `${d.identityCard}` : null,
            cccd: d.identityCard.length === 12 ? `${d.identityCard}` : null,
            phoneNumber: d.phoneNumber,
            dateOfBirth: d.dateOfBirth,
            hasYearOfBirthOnly: false,
            profileCreationReason: {},
